    private void SearchBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SearchBtnActionPerformed
        
        String patientID = idTextField.getText();
        try {
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3307/hospitalmanagementdb","root","");
            Statement st = con.createStatement(); 
            ResultSet rs = st.executeQuery("select * from PatientTable where PatientID ='"+patientID+"'");
            if(rs.next()) {
                nameTextField.setText(rs.getString(2));
                numberTextField.setText(rs.getString(3));
                ageTextField.setText(rs.getString(4));
                genderTF.setText(rs.getString(5));
                genotypeTF.setText(rs.getString(6));
                bloodGroupTF.setText(rs.getString(7));
                addressTextField.setText(rs.getString(8));
                previousAilmentTextField.setText(rs.getString(9));
                idTextField.setEditable(false);
            }
            else {
                JOptionPane.showMessageDialog(this, "Patient ID Does Not Exist");
            }
        }
         catch (Exception e) {
             JOptionPane.showMessageDialog(null, "Connection Error");
         }
    }//GEN-LAST:event_SearchBtnActionPerformed

