    private void UpdateBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_UpdateBtnActionPerformed
        
        String patientID = idTextField.getText();
        String name = nameTextField.getText();
        String contactNumber = numberTextField.getText();
        String age = ageTextField.getText();
        String gender = genderTF.getText();
        String genotype = genotypeTF.getText();
        String bloodGroup = bloodGroupTF.getText();
        String address = addressTextField.getText();
        String previousAilment = previousAilmentTextField.getText();
         try {
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3307/hospitalmanagementdb","root","");
            Statement st = con.createStatement(); 
            st.executeUpdate("update PatientTable set Name ='"+name+"', ContactNumber ='"+contactNumber+"',"
                    + "Age ='"+age+"', Gender ='"+gender+"', Genotype ='"+genotype+"',"
                            + "BloodGroup ='"+bloodGroup+"', Address ='"+address+"', "
                                    + "AnyPreviousAilment ='"+previousAilment+"' where PatientID ='"+patientID+"'");
            JOptionPane.showMessageDialog(this, "Record Successfully Updated");
            setVisible(false);
            new updatePatientRecord().setVisible(true);
        }
         catch (Exception e) {
             JOptionPane.showMessageDialog(null, "Please enter data in correct format");
         }
        
    }//GEN-LAST:event_UpdateBtnActionPerformed

